import React from "react";
import {
  Text,
  TextInput,
  StyleSheet,
  View,
  TouchableOpacity,
} from "react-native";

const Calculadora2 = () => {
  const [valor, setValor] = React.useState(0);
  return (
    <>
      <View style={styles.container}>
        <TouchableOpacity style={styles.buttonDelete}>
          <Text style={styles.buttonText}>C</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.container}>
        <TextInput
          style={styles.input}
          onChangeText={(valor) => setValor(valor)}
          value={valor.toString()}
          keyboardType="numeric"
        />
      </View>
      <View style={styles.buttonArea}>
        {/* Botões 1, 2 e 3 */}

        <View style={styles.buttonrow}>
          <TouchableOpacity style={styles.button}>
            <Text style={styles.buttonText}>1</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button}>
            <Text style={styles.buttonText}>2</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button}>
            <Text style={styles.buttonText}>3</Text>
          </TouchableOpacity>
        </View>
{/* Fim dos botões 4,5  */}
        <View style={styles.buttonrow}>
          <TouchableOpacity style={styles.button}>
            <Text style={styles.buttonText}>4</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button}>
            <Text style={styles.buttonText}>5</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button}>
            <Text style={styles.buttonText}>6</Text>
          </TouchableOpacity>
        </View>
{/* Fim dos botões de números */}
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  textStyle: {
    fontSize: 36,
    color: "Black",
    fontWeight: "bold",
    textAlign: "center",
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 20,
  },
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  button: {
    width: "33%",
    backgroundColor: "black",
    paddingVertical: 10,
    alignItems: "center",
    textAlign: "center",
    borderRadius: 5,
    marginHorizontal: 0,
  },
  buttonDelete: {
    backgroundColor: "red",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 5,
    width: 100,
    height: 100,
  },

  buttonText: {
    color: "white",
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
  },

  buttonrow: {
    flexDirection: "row",
  },
  buttonArea:{

  }
});

export default Calculadora2;
