import React from 'react'
import { Text, StyleSheet, View,TouchableOpacity,ImageBackground,Dimensions} from 'react-native'

const HomeScreen = (props) => {
  console.log(props)
  const navigation = props.navigation
  const windowWidth = Dimensions.get('window').width;
  const buttonWidth = windowWidth * 0.5;

  return (
    <ImageBackground
    source={require('./images/backgroundcalculadora.jpg')}
    style={styles.background} >

    <View   style={styles.principal}>
    <TouchableOpacity
    onPress={() => {
      console.log('button pressed');
      navigation.navigate('Calculadora1');
    }}
    style={[styles.button,  { width: buttonWidth }]}
  >
    <Text style={styles.buttonText}>CALCULADORA 1</Text>
  </TouchableOpacity>

  <TouchableOpacity
    onPress={() => {
      console.log('button pressed');
      navigation.navigate('Calculadora2');
    }}
    style={[styles.button,  { width: buttonWidth }]}
  >
    <Text style={styles.buttonText}>CALCULADORA 2</Text>
  </TouchableOpacity>

    </View>
    </ImageBackground>
  )
}

const styles = StyleSheet.create({

 button:{
  backgroundColor: 'black',
  padding: 15,
  alignItems:'center',
  borderRadius: 5,
 } ,
 buttonText: {
  color: 'white',
  fontSize: 18,
  fontWeight:'bold'
},
  background: {
    flex: 1,
    resizeMode: 'cover', 
    width: Dimensions.get('window').width,
    width: Dimensions.get('window').height,
  },
  principal: {
    flex: 1,
    justifyContent: 'space-evenly',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.6)', 
  },
})

export default HomeScreen
