import React from 'react'
import { Text, StyleSheet, View, Button,ImageBackground} from 'react-native'

const HomeScreen = (props) => {
  console.log(props)
  const navigation = props.navigation

  return (
    <ImageBackground
    source={require('./images/backgroundcalculadora.jpg')}
    style={styles.background} >

    <View   style={styles.principal}>
      <Text style={styles.text}>MATEUS DE PAULA CALCULATOR 2.O </Text>
      <View   style={styles.btn}>
      <Button 
        onPress={() => {
          console.log('button pressed')
          navigation.navigate('Calculadora1')
        }}
        title="CALCULADORA 1" color={'black'}/>

      </View>

      <View   style={styles.btn} >
      <Button 
        onPress={() => {
          console.log('button pressed')
          navigation.navigate('Calculadora2')
        }}
        title="CALCULADORA 2" color={'black'}/>
      </View>
    </View>
    </ImageBackground>
  )
}

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    textAlign: 'center',
    color: 'white',
    fontWeight:'bold',
    marginVertical:'50'
  },
  btn: {
    marginVertical :25 ,
  },
  background: {
    flex: 1,
    resizeMode: 'cover', 
  },
  principal: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', 
  },
})

export default HomeScreen
