import React from 'react'; 
import { Text,TextInput, StyleSheet, View,TouchableOpacity } from 'react-native'; 

const Calculadora2 = () => {
  const [valor, setValor] = React.useState(0);
  return (
    <View>
     <TextInput
     style={styles.input}
         onChangeText={(valor) => setValor(valor)}
         value={valor.toString()}
         keyboardType="numeric"/>

    </View>
  )
}

const styles = StyleSheet.create({
  textStyle: {
    fontSize: 36,
    color: 'Black',
    fontWeight:'bold',
    textAlign:'center',
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 20,
  },
  container: {
    flex: 1,
    justifyContent: 'space-evenly',
    alignItems: 'center',
  },
  button: {
    backgroundColor: 'black',
    paddingVertical:10,
    alignItems: 'center',
    textAlign:'center',
    borderRadius: 5,
    marginHorizontal:0,
  },
  buttonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
  buttonArea:{
    flex: 1,
    justifyContent: 'space-evenly',
    width:'100%',
    paddingHorizontal:30,
    marginVertical:10,
  }
})

export default Calculadora2
