// PART 1 - IMPORT LIBRARIES
import React from 'react'; // importing entire library
import { Text,TextInput, StyleSheet, View } from 'react-native'; // importing some piees of react-native

// PART 2 - CREATE A COMPONENT 
// A FUNCTION THAT RETURNS SOME JSX
const Calculadora1 = () => {
  const [text, onChangeText] = React.useState('Useless Text');

  return (
    <View>
      <Text style={styles.textStyle}>Valor A:</Text>
      <TextInput
        style={styles.input}
        onChangeText={onChangeText}
        value={text}
      />
      <Text style={styles.textStyle}>this is the components screen</Text>
      <TextInput
        style={styles.input}
        onChangeText={onChangeText}
        value={text}
      />

    </View>
  )
}

const styles = StyleSheet.create({
  textStyle: {
    fontSize: 36,
    color: 'red'
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
})


export default Calculadora1
