// PART 1 - IMPORT LIBRARIES
import React from 'react'; // importing entire library
import { Text,TextInput, StyleSheet, View } from 'react-native'; // importing some piees of react-native

// PART 2 - CREATE A COMPONENT 
// A FUNCTION THAT RETURNS SOME JSX
const Calculadora1 = () => {
  const [text, setText] = React.useState(0);

  return (
    <View style={styles.container}>
      <Text style={styles.textStyle}>Valor A:</Text>
      <TextInput
        style={styles.input}
        onChange={text => setText()}
        value={text}
      />
      <Text style={styles.textStyle}>Valor B:</Text>
      <TextInput
        style={styles.input}
        onChange={text => setText()}
        value={text}
      />

    </View>
  )
}

const styles = StyleSheet.create({
  textStyle: {
    fontSize: 36,
    color: 'Black',
    fontWeight:'bold',
  },
  input: {

    height: 40,
    borderWidth: 1,
    padding: 10,
  },
  container: {
    flex: 1,
    justifyContent: 'flex-start',
    width:'100%',
    alignItems: 'center',
  },
})


export default Calculadora1
